<?php

namespace AppBundle\Repository;

use Doctrine\ORM\EntityRepository;
use Doctrine\ORM\Query\ResultSetMapping;

/**
 * LivingPlaceRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class LivingPlaceRepository extends EntityRepository
{

    public function getInfos()
    {
       return $this->createQueryBuilder('l')
            ->select('l.coordinates', 'l.activityCode', 'l.area', 'l.id', 'l.district', 'l.wayType', 'l.situation')
           ->getQuery()
           ->getResult();
    }

    public function findPaginated(int $rows = null, int $offset = null)
    {
        return $this->createQueryBuilder('l')
            ->select('l.coordinates', 'l.hints')
            ->where('l.hints IS NOT NULL')
            ->getQuery()
            ->setFirstResult($offset)
            ->setMaxResults($rows)
            ->getResult();
    }

    public function getFrequency()
    {
        return $this->createQueryBuilder('l')
            ->select('l.frequency')
            ->where('l.frequency IS NOT NULL')
            ->getQuery()
            ->getResult();
    }

    public function getFrequenciesAndId()
    {
        return $this->createQueryBuilder('l')
            ->select('l.id', 'l.frequency')
            ->andWhere('l.frequency IS NOT NULL')
            ->getQuery()
            ->getResult();
    }
}
