<?php

namespace AppBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * TouristicPlaceRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TouristicPlaceRepository extends EntityRepository
{

    public function getInfos()
    {
        return $this->createQueryBuilder('t')
            ->select('t.frequency', 't.geoPoint2d')
            ->getQuery()
            ->getResult();
    }

    public function findPaginated(int $rows = null, int $offset = null)
    {
       return $this->createQueryBuilder('t')
        ->select('t.hints', 't.coordinates')
        ->getQuery()
        ->setFirstResult($offset)
        ->setMaxResults($rows)
        ->getResult();
    }

    public function getFrequency()
    {
        return $this->createQueryBuilder('t')
            ->select('t.frequency')
            ->where('t.frequency IS NOT NULL')
            ->getQuery()
            ->getResult();
    }

    public function getFrequenciesAndId()
    {
        return $this->createQueryBuilder('t')
            ->select('t.id', 't.frequency')
            ->andWhere('t.frequency IS NOT NULL')
            ->getQuery()
            ->getResult();
    }
}
